template.value:
// --- Contoh Literals di Java ---

// 1. Integer Literals (angka bulat)
int angkaBulat = 123;
int angkaNegatif = -45;

// 2. Floating-Point Literals (angka desimal)
double pi = 3.14;
double sainsNotasi = 1.25e-4; // sama dengan 0.000125

// 3. Character Literals (karakter tunggal, pakai tanda kutip satu)
char huruf = 'A';
char simbol = '#';

// 4. String Literals (teks, pakai tanda kutip dua)
String nama = "IntelliJ IDEA";
String kalimat = "Ini adalah sebuah kalimat.";

// 5. Boolean Literals (hanya ada dua nilai)
boolean isActive = true;
boolean isFinished = false;

// 6. Null Literal (berarti "tidak ada objek")
String kosong = null;

// --- Akhir dari contoh ---
$END$

// --- Contoh Penggunaan Underscore di Java ---

// 1. Pemisah Angka (Numeric Literals) - Java 7+
// Membuat angka besar lebih mudah dibaca.
long satuMiliar = 1_000_000_000L;
long nomorKartuKredit = 1234_5678_9012_3456L;
double pi = 3.141_592_653;

// 2. Variabel Buangan (Discard Variable) - Java 21+
// Menandai bahwa sebuah nilai sengaja tidak digunakan.

// Contoh di try-with-resources:
// Kita hanya butuh side-effect dari membuat resource, bukan resource-nya.
try (var _ = new SomeAutoCloseableResource()) {
    System.out.println("Kode di dalam blok try dijalankan.");
}

// Contoh di Pattern Matching (jika sudah support):
// if (point instanceof Point(int x, int _)) {
//     System.out.println("Hanya koordinat x yang penting: " + x);
// }

// --- Akhir dari contoh ---
$END$

// --- Contoh Konversi Tipe Data Number di Java ---

// 1. Widening (Melebar) - OTOMATIS, aman
int nilaiInt = 100;
double nilaiDouble = nilaiInt; // int otomatis menjadi 100.0

// 2. Narrowing (Menyempit) - MANUAL, ada risiko kehilangan data
double harga = 99.99;
int hargaBulat = (int) harga; // Hasilnya akan menjadi 99 (desimal dibuang)

// 3. String ke Number (Parsing)
String angkaString = "250";
int angkaInt = Integer.parseInt(angkaString);

String desimalString = "3.14";
double angkaDouble = Double.parseDouble(desimalString);

// 4. Number ke String
int usia = 30;
String usiaString = String.valueOf(usia); // Cara yang direkomendasikan

double pi = 3.14159;
String piString = Double.toString(pi);     // Cara alternatif

// --- Akhir dari contoh ---
$END$

char karakter = $CHAR$;

// --- Contoh Penggunaan Class Character ---

// 1. Memeriksa tipe karakter
boolean adalahDigit = Character.isDigit(karakter);
boolean adalahHuruf = Character.isLetter(karakter);
boolean adalahSpasi = Character.isWhitespace(karakter);

// 2. Mengubah huruf besar/kecil
char hurufBesar = Character.toUpperCase(karakter);
char hurufKecil = Character.toLowerCase(karakter);

// 3. Mendapatkan nilai numerik (jika karakter adalah angka)
int nilaiAngka = Character.getNumericValue(karakter);

// --- Akhir dari contoh ---
$END$

if ($CONDITION$) {
    // kode dijalankan jika kondisi bernilai true
    $END$     } else {
    // kode dijalankan jika kondisi bernilai false
}

// --- Contoh Logika Boolean di Java ---

// 1. Deklarasi
boolean isLearner = true;
boolean isFinished = false;

// 2. Operator Perbandingan (hasilnya selalu boolean)
int a = 5;
int b = 10;
boolean aLebihBesar = (a > b); // false

// 3. Operator Logika
boolean kondisi1 = true;
boolean kondisi2 = false;

// AND (&&): true jika kedua-duanya true
boolean hasilAND = kondisi1 && kondisi2; // false

// OR (||): true jika salah satunya true
boolean hasilOR = kondisi1 || kondisi2;  // true

// NOT (!): membalik nilai
boolean hasilNOT = !kondisi1;           // false

// 4. Penggunaan dalam if-else
if (isLearner && !isFinished) {
    System.out.println("Terus belajar!");
}

// --- Akhir dari contoh ---
$END$

// --- Contoh Operasi String di Java ---

String teks = "  Hello, World!  ";

// 1. Menggabungkan String (Concatenation)
String sapaan = teks + " Selamat Belajar!";

// 2. Mendapatkan Panjang String
int panjang = teks.length(); // Hasil: 16 (termasuk spasi)

// 3. Mengambil Karakter di Posisi Tertentu
char hurufPertama = teks.charAt(0); // Hasil: ' ' (spasi)

// 4. Mengambil Bagian String (Substring)
String dunia = teks.substring(7, 12); // Hasil: "World"

// 5. Mencari atau Memeriksa Isi String
boolean adaKoma = teks.contains(",");
boolean diawaliHello = teks.trim().startsWith("Hello"); // trim() untuk hapus spasi
boolean diakhiriTandaSeru = teks.endsWith("!");

// 6. Mengganti Bagian String
String diganti = teks.replace("World", "IntelliJ");

// 7. Mengubah Huruf Besar/Kecil
String hurufBesar = teks.toUpperCase();
String hurufKecil = teks.toLowerCase();

// 8. Memecah String (Split)
String kalimat = "satu,dua,tiga";
String[] kata = kalimat.split(","); // Hasil: ["satu", "dua", "tiga"]

// 9. Menghapus Spasi di Awal dan Akhir
String bersih = teks.trim(); // Hasil: "Hello, World!"

// 10. PERINGATAN: Membandingkan String
String a = "test";
String b = "test";
String c = new String("test");

// Gunakan .equals() untuk membandingkan ISI!
boolean isSama1 = a.equals(b);  // true
boolean isSama2 = a.equals(c);  // true

// JANGAN PAKAI '==' untuk membandingkan ISI String!
boolean isSama3 = (a == c);    // false! (karena '==' cek lokasi memori)

// --- Akhir dari contoh ---
$END$

