package programmer_zaman_now.belajar_spring_dasar;

import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;

@Configuration
public class HeloWordConfiguration {

    @Bean
    public String helloWorld() {
        return "Hello, Spring World!";
    }

}

package programmer_zaman_now.belajar_spring_dasar;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;
import org.springframework.context.annotation.AnnotationConfigApplicationContext;

public class AplicationContextTest {

    @Test
    void testAplicationContext() {

        AnnotationConfigApplicationContext context = new AnnotationConfigApplicationContext(HeloWordConfiguration.class);

        System.out.println(context);

        Assertions.assertNotNull(context);

    }
}
