import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;

@Configuration
public class HeloWordConfiguration {

    @Bean
    public String helloWorld() {
        return "Hello, Spring World!";
    }

}

package programmer_zaman_now.belajar_spring_dasar;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;
import org.springframework.context.annotation.AnnotationConfigApplicationContext;

public class AplicationContextTest {

    @Test
    void testAplicationContext() {

        AnnotationConfigApplicationContext context = new AnnotationConfigApplicationContext(HeloWordConfiguration.class);

        System.out.println(context);

        Assertions.assertNotNull(context);

    }
}

package programmer_zaman_now.belajar_spring_dasar;

public class Database {

    private Database() {
        // Private constructor
    }

    /**
     * The inner class is not loaded until getInstance() is called.
     * This provides lazy-loading and is thread-safe by the JVM.
     */
    private static class SingletonHolder {
        private static final Database INSTANCE = new Database();
    }

    public static Database getInstance() {
        return SingletonHolder.INSTANCE;
    }
}

package programmer_zaman_now.belajar_spring_dasar;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;
import org.springframework.context.annotation.AnnotationConfigApplicationContext;


public class AplicationContextTest {

@Test
void testSingletonDatabase(){
    Database database1 = Database.getInstance();
    Database database2 = Database.getInstance();

    Assertions.assertSame(database1, database2);

}

